class Example
{
 def log
 def context
 def testRunner
 // Class constructor with same case as Class name
 def Example(logIn,contextIn,testRunnerIn)
 {
  this.log = logIn
  this.context = contextIn
  this.testRunner = testRunnerIn
 }
}
