import com.mindtree.client.*;

/*
 * Current Time
 */
def currentDate = Instant.now();
/*
 * Time stamp 20 min before
 */
def time20minutebefore=Instant.now().minus(Duration.ofSeconds(20*60));
testRunner.testCase.testSuite.setPropertyValue("timestamp",currentDate.toString()); 
testRunner.testCase.testSuite.setPropertyValue("timestamp20minutebefore",time20minutebefore.toString());


/*
 * Setting Custom Property
 */
JsonMapper mapper=new JsonMapper();
mapper.JsonMapper1(testRunner,"InputData.json");

